    overflow: hidden;
    width: 100%;
}

.carousel-track {
    display: flex;
    transition: transform 0.3s ease;
    will-change: transform;
}

.carousel-item {
    min-width: 150px;
    height: 50px;
    display: flex;
    align-items: center;
    color: white;
    justify-content: center;
    margin: 0 10px;
    border-radius: 5px;
}

.carousel-button {
    background: none;
    border: none;
    font-size: 2em;
    cursor: pointer;
    user-select: none;
